declare @dbname nvarchar(MAX);
declare @filtertable nvarchar(MAX);
declare @sql nvarchar(max);

set @dbname = 'TRUX_CD_COMP,TRUX_DC_COMP,TRUX_ES_COMP,TRUX_HR_COMP,TRUX_LR_COMP,TRUX_MC_COMP,TRUX_MH_COMP,TRUX_MO_COMP,TRUX_MP_COMP,TRUX_MW_COMP,TRUX_NI_COMP,TRUX_PS_COMP,TRUX_RS_COMP,TRUX_RT_COMP,TRUX_RZ_COMP,TRUX_SH_COMP,TRUX_SW_COMP,TRUX_TP_COMP,TRUX_WA_COMP'
set @filtertable = 'RM05,SM01,DM01,DM01p,DM05A,DM05B,DM05FT,DM05Z,LM02,MT02,RM03U'

create table #ConsolidatedTable
( Dbname nvarchar(max),
SchemaName nvarchar(max),
TableName nvarchar(max),
CreatedDate datetime,
LastUpdatedDatetime datetime
);

declare @temptable table (DatabaseName nvarchar(128));

insert into @temptable
select value from string_split(@dbname, ',');

declare @CurrentDatabase nvarchar(128);

declare db_cursor cursor for
select DatabaseName
from @temptable


open db_cursor

fetch next from db_cursor into @CurrentDatabase;


while @@FETCH_STATUS = 0
begin
	set @sql = N'USE ' + QUOTENAME(@CurrentDatabase) + ';
	insert into #ConsolidatedTable
	select
		''' + @CurrentDatabase + ''' as DBname,
		SCHEMA_NAME(schema_id) as SchemaName,
		name as TableName,
		CREATE_DATE as CreatedDate,
		MODIFY_DATE as LastUpdatedDatetime
	from sys.tables
	where name in (''' + replace(@filtertable, ',',''',''') + ''')
	order by name;';


	exec sp_executesql @sql;

	fetch next from db_cursor into @CurrentDatabase;
END

close db_cursor;
deallocate db_cursor;

select * from #ConsolidatedTable

